class Producto:    def __init__(self,precio,codigo_P):        self.__precio=precio        self.__codogo=codigo_P    def get_precio(self):        return (self.__precio)    def get_codigo(self):        return (self.__codogo)class Ejemplar:    def __init__(self,codigo,titulo):        self.__codigo=codigo        self.__titulo=titulo    def get_codigo(self):        return self.__codigo    def get_titulo(self):        return  self.__titulo    def __str__(self):        return "codigo:"  +self.__codigo + " titulo:" + self.__titulo#la constructora con una herencia multiple(la clase Ejemplar y Producto)class Libro(Ejemplar,Producto):    def __init__(self,codigo,titulo,editoria, autor,precio,codigo_P):        Ejemplar.__init__(self,codigo,titulo)        Producto.__init__(self,precio,codigo_P)        self.__editoria = editoria        self.__autor = autorclass Disco(Ejemplar):    def __init__(self,codigo,titulo,artista, discografia):        super().__init__(codigo,titulo)        self.__artista=artista        self.__discografia=discografia    #invocamos el str de la super clase(Ejemplar)    #def __str__(self):     #   return super().__str__()+"artista:"+self.__artista+" Discografia "+self.__discografia    def __str__(self):        return super().__str__()+"autor:"+self.__artista#ejemplar=Ejemplar("123","El se√±or de los anillos")disco_1=Disco("234","sotana ","angel_glamur","ANG RECCORD")#print(ejemplar)print(disco_1)libro_1=Libro("123","El codigo Davinchi")print(libro_1)